def display_error(error, lang):
    if lang == 1: # swahili
        if error == 0:
            return "Chaguo lisilo halali.\n" 
        else:
            return ""
    else: # default engilsh
        if error == 0: 
            return "Invalid option.\n"
        else:
            return ""

